import {Table} from 'react-bootstrap';
import {UserEntry} from '../model/UserEntry';
import ModalUser from './ModalUser';

function List(props: {items: UserEntry[], onStatusChange: (user: UserEntry) => void,editUser: (id:number) => void}) {
  
  const rows = props.items.map((user) => (
    <tr key={user.id}>
      <td>{user.vorname}</td>
      <td>{user.nachname}</td>
      <td>{user.beschreibung}</td>
      <td>{user.date.toISOString()}</td>
      <td>
        <button className="btn btn-danger" onClick={() => {props.onStatusChange(user)}}>
          delete
        </button>
      </td> 
      <td>   
        <ModalUser onUser={(e:any)=>{updateItem(e.vorname,e.nachname,e.beschreibung)}}/>
      </td>
      
    </tr>
  ));

  return (
    <Table>
      <thead>
      <tr>
        <th>Vorname</th>
        <th>Nachname</th>
        <th>Beschreibung</th>
        <th>ErstellungsDatum</th>
        <th>Action</th>
      </tr>
      </thead>
      <tbody>
      {rows}
      </tbody>
    </Table>
  );
}

export default List;
