import React from 'react';
import './App.css';
import {Navbar, Button, Row, Col, Container} from 'react-bootstrap';
import {UserEntry} from './model/UserEntry';
import List from './components/ListUser';
import FormUser from './components/AddUser';

class App extends React.Component<{}, { title: string, todos: UserEntry[] }> {
  constructor(props: {}) {
    super(props);
    this.state = {
      title: 'UserManager', todos: [
        new UserEntry('kevin','Piam','student'),
        new UserEntry('Winnie','Tongle','student'),
        new UserEntry('Maxwell','Tchiabe','hhh')
      ]
    }
  }

  changeStatus(todo: UserEntry) {
    this.setState(state => ({
      todos: state.todos.filter(item =>
        item.id !== todo.id 
      )
    }));
  }
  

  createToDo(vorname: string, nachname: string, beschreibung: string) {
    this.setState(state => ({
      todos: [...state.todos, new UserEntry(vorname,nachname,beschreibung)]
    }));
  }
  
