 }

  render() {
    return <div>
      <Navbar bg="light">
        <Navbar.Brand>{this.state.title}</Navbar.Brand>
      </Navbar>
      <Container>
        <Row>
          <Col md={9}>
            <h3>UserList</h3>
            <List items={this.state.todos} onStatusChange={(todo) => this.changeStatus(todo) } updateItem ={(id,vorname,nachname,beschreibung) => this.editUser(id,vorname,nachname,beschreibung)}/>
          </Col>
          <Col md={6}>
            <FormUser onItem={(e:any)=>{this.createToDo(e.vorname,e.nachname,e.beschreibung)}}/>
          </Col>
        </Row>
      
      </Container>

    </div>;
  }
}

export default App;
