import {Button, Form} from 'react-bootstrap';
import {useState} from 'react';

function FormUser(props:any) {
    
    const [vorname, setVorname] = useState('');
    const [nachname, setNachname] = useState('');
    const [beschreibung, setBeschreibung] = useState('');


    const handleChangeVorname =(e:any)=> {
      setVorname(e.target.value);

    }
    const handleChangeNachname =(e:any)=> {
      setNachname(e.target.value);
    }
    const handleChangebeschreibung=(e:any)=> {
      setBeschreibung(e.target.value);
    }
  
    const handleSubmit=(e:any)=> {
      e.preventDefault()
      props.onItem({ 
        vorname,
        nachname,
        beschreibung
      });
    }
    
    return (
      <div>
        <h3>neue User</h3>
        <Form onSubmit={(e) => handleSubmit(e)}>
          <Form.Control value={vorname}
                        onChange={(e) => handleChangeVorname(e)}
                        type="text" name="vorname"placeholder="Vorname" style={{margin: '20px 0'}}/>
          <Form.Control value={nachname}
                        onChange={(e) => handleChangeNachname(e)}
                        type="text" name="nachname" placeholder="Nachname" style={{margin: '20px 0'}}/>       
          <Form.Control value={beschreibung}
                        onChange={(e) => handleChangebeschreibung(e)}
                        type="text" name="beschreibung" placeholder="Beschreibung" style={{margin: '20px 0'}}/>                      
          <Button variant="primary" type="submit">
          
            Submit
          </Button>
        </Form>
      </div>
    );
    
}
export default FormUser;
