import {Modal,Button} from 'react-bootstrap';
import { useState } from 'react';
import List from './ListUser';
import App from '../App';

function ModalUser(props: any) {
  console.log(props);
    
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
      
    const [vorname, setVorname] = useState(p);
    const [nachname, setNachname] = useState('');
    const [beschreibung, setBeschreibung] = useState('');


    const handleChangeVorname =(e:any)=> {
      setVorname(e.target.value);

    }
    const handleChangeNachname =(e:any)=> {
      setNachname(e.target.value);
    }
    const handleChangebeschreibung=(e:any)=> {
      setBeschreibung(e.target.value);
    }
  
    const handleSubmit=(e:any)=> {
      e.preventDefault()
      console.log("test");
      props.onItem({ 
        vorname,
        nachname,
        beschreibung
      });
    }
    
   
    return (

      
      <>
        <Button variant="primary" onClick={handleShow}>
          bearbeiten
        </Button>
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>User Bearbeiten</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <form >
              <div className="form-group">
                  <label >VorName:</label>
                <input className="form-control" id="vorname" type="text" name="vorname"  value={props.onUser.vorname} onChange={e => handleChangeVorname(e)}/>
              </div>
              <div className="form-group">
                  <label htmlFor="name">Nachname:</label>
                <input className="form-control" id="nachname" value={props.onUser.nachname}/>
              </div>
              <div className="form-group">
                  <label>Beschreibung:</label>
                <input className="form-control" id="beschreibung"  value={props.onUser.beschreibung}/>
              </div>           
           </form>
            
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Schlie√üen
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Speichern
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
  
  export default ModalUser;
