import {Modal,Button} from 'react-bootstrap';
import { useState } from 'react';
import List from './ListUser';
import App from '../App';

function ModalUser(props: any) {
  console.log(prop)
    const [vorname, setVorname] = useState('');
    const [nachname, setNachname] = useState('');
    const [beschreibung, setBeschreibung] = useState('');
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    
    const handleChangeVorname =(e:any)=> {
      setVorname(e.target.value);

    }
    const handleChangeNachname =(e:any)=> {
      setNachname(e.target.value);
    }
    const handleChangebeschreibung=(e:any)=> {
      setBeschreibung(e.target.value);
    }
  
  
    return (
      
      <>
        <Button variant="primary" onClick={handleShow}>
          bearbeiten
        </Button>
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>User Bearbeiten</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <form >
              <div className="form-group">
                  <label >Name:</label>
                <input className="form-control" id="vorname" value={props.vorname}/>
              </div>
              <div className="form-group">
                  <label htmlFor="name">Nachname:</label>
                <input className="form-control" id="nachname" value={props.nachname}/>
              </div>
              <div className="form-group">
                  <label>Beschreibung:</label>
                <input className="form-control" id="beschreibung"  value={props.beschreibung}/>
              </div>           
           </form>
            
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Schließen
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Speichern
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
  
  export default ModalUser;
