import {Button, Form} from 'react-bootstrap';
import React, {ChangeEvent, FormEvent} from 'react';

export class Add extends React.Component <{ onCreate: (vorname: string, nachname: string , beschreibung: string ) => void },
 { value: string , value1: string , value2: string}> {
  constructor( props: { onCreate: (vorname: string, nachname: string , beschreibung: string) => void }) {
    super(props);
    this.state = {value: '', value1: '' , value2: ''};
  }

  handleChangeVorname(event: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) {
    this.setState({value: event.target.value});
  }
  handleChangeNachname(event: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) {
    this.setState({value: event.target.value });
  }
  handleChangeNachname(event: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) {
    this.setState({value: event.target.value });
  }

  handleSubmit(event: FormEvent<HTMLFormElement>) {
    event.preventDefault()
    this.props.onCreate(this.state.value, this.state.value1,this.state.value2);
  }

  render() {
    return (
      <div>
        <h3>New element</h3>
        <Form onSubmit={(e) => this.handleSubmit(e)}>
          <Form.Control value={this.state.value}
                        onChange={(e) => this.handleChange(e)}
                        type="text" placeholder="Vorname" style={{margin: '20px 0'}}/>
          <Form.Control value={this.state.value1}
                        onChange={(e) => this.handleChange(e)}
                        type="text" placeholder="Nachname" style={{margin: '20px 0'}}/>       
          <Form.Control value={this.state.value2}
                        onChange={(e) => this.handleChange(e)}
                        type="text" placeholder="Beschreibung" style={{margin: '20px 0'}}/>                      
          <Button variant="primary" type="submit">

            Submit
          </Button>
        </Form>
      </div>
    );
  }
}

export default Add;
