import React from 'react';
import './App.css';
import {Navbar, Button, Row, Col, Container} from 'react-bootstrap';
import {ToDoEntry} from './model/ToDoEntry';
import List from './components/List';
import Add from './components/Add';

class App extends React.Component<{}, { title: string, todos: ToDoEntry[] }> {
  constructor(props: {}) {
    super(props);
    this.state = {
      title: 'To-Do-Liste', todos: [
        new ToDoEntry('Butter'),
        new ToDoEntry('Mehl'),
        new ToDoEntry('Eier')
      ]
    }
  }

  changeStatus(todo: ToDoEntry) {
    this.setState(state => ({
      todos: state.todos.map(item =>
        item.id === todo.id ? {...item, done: !item.done} as ToDoEntry : item
      )
    }));
  }

  createToDo(title: string) {
    this.setState(state => ({
      todos: [...state.todos, new ToDoEntry(title)]
    }));
  }

  render() {
    return <div>
      <Navbar bg="light">
        <Navbar.Brand>{this.state.title}</Navbar.Brand>
      </Navbar>
      <Container>
        <Row>
          <Col md={9}>
            <h3>ToDo</h3>
            <List items={this.state.todos.filter(todo => !todo.done)}
                  onStatusChange={(todo) => this.changeStatus(todo)}/>
          </Col>
          <Col md={3}>
            <Add onCreate={(title) => this.createToDo(title)}/>
          </Col>
        </Row>
        <h3>Done</h3>
        <List items={this.state.todos.filter(todo => todo.done)} onStatusChange={(todo) => this.changeStatus(todo)}/>
      </Container>

    </div>;
  }
}

export default App;
