import React from 'react';
import './App.css';
import {Navbar, Button, Row, Col, Container} from 'react-bootstrap';
import {ToDoEntry} from './model/ToDoEntry';
import List from './components/List';
import FormUser from './components/Add';

class App extends React.Component<{}, { title: string, todos: ToDoEntry[] }> {
  constructor(props: {}) {
    super(props);
    this.state = {
      title: 'UserManager', todos: [
        new ToDoEntry('kevin','Piam','student'),
        new ToDoEntry('Winnie','Tongle','student'),
        new ToDoEntry('Maxwell','Tchiabe','hhh')
      ]
    }
  }

  changeStatus(todo: ToDoEntry) {
    this.setState(state => ({
      todos: state.todos.filter(item =>
        item.id !== todo.id 
      )
    }));
  }

  createToDo(vorname: string, nachname: string, beschreibung: string) {
    console.log("hallo");
    this.setState(state => ({
      todos: [...state.todos, new ToDoEntry(vorname,nachname,beschreibung)]
    }));
  }

 editUser(id:number){
  
    this.state.todos.forEach((user) => {
      if(user.id === id){
      console.log(user.id)
      user.vorname="winnie";
      user.nachname="carole";
      user.beschreibung="hhhhd";
      console.log(user.nachname+'',user.vorname+'', user.beschreibung);
      }
   })
   
 }

  render() {
    return <div>
      <Navbar bg="light">
        <Navbar.Brand>{this.state.title}</Navbar.Brand>
      </Navbar>
      <Container>
        <Row>
          <Col md={9}>
            <h3>UserList</h3>
            <List items={this.state.todos} onStatusChange={(todo) => this.changeStatus(todo) }onEditChange={(id) => this.editUser(id)}/>
          </Col>
          <Col md={6}>
            <FormUser onItem={(e)=>{al}}/>
          </Col>
        </Row>
      
      </Container>

    </div>;
  }
}

export default App;
