import React from 'react';
import './App.css';
import {Navbar, Button, Row, Col, Container} from 'react-bootstrap';
import {UserEntry} from './model/UserEntry';
import List from './components/ListUser';
import FormUser from './components/AddUser';
import { Route, BrowserRouter as Router, Link } from "react-router-dom";

class App extends React.Component<{}, { title: string, todos: UserEntry[] }> {
  constructor(props: {}) {
    super(props);
    this.state = {
      title: 'UserManager', todos: [
        new UserEntry('kevin','Piam','student'),
        new UserEntry('Winnie','Tongle','student'),
        new UserEntry('Maxwell','Tchiabe','hhh')
      ]
    }
  }

  changeStatus(todo: UserEntry) {
    this.setState(state => ({
      todos: state.todos.filter(item =>
        item.id !== todo.id 
      )
    }));
  }
  

  createToDo(vorname: string, nachname: string, beschreibung: string) {
    this.setState(state => ({
      todos: [...state.todos, new UserEntry(vorname,nachname,beschreibung)]
    }));
  }
  
 editUser(user:UserEntry,vorname: string, nachname: string, beschreibung: string){ 
    for(let i=0;i<this.state.todos.length;i++){
        if(this.state.todos[i].id===user.id){
          this.state.todos[i].vorname = vorname;
          this.state.todos[i].nachname = nachname;
          this.state.todos[i].beschreibung = beschreibung;
        }
    }
    this.setState(state => ({ 
      todos: this.state.todos
    }));
 }
 

  render() {
    return <div>
      <Navbar bg="dark">
        <Navbar.Brand color="white">{this.state.title}</Navbar.Brand>
        <Navbar.Brand>User List</Navbar.Brand>
        <Navbar.Brand>User Hinzuf√ºgen</Navbar.Brand>
      </Navbar>
      <Container>
        <Row>
          <Col md={9}>
            <h3>UserList</h3>
            <List items={this.state.todos} onStatusChange={(todo) => this.changeStatus(todo) } updateItem ={(user,vorname,nachname,beschreibung) => this.editUser(user,vorname,nachname,beschreibung)}/>
          </Col>
          <Col md={6}>
            <FormUser onItem={(e:any)=>{this.createToDo(e.vorname,e.nachname,e.beschreibung)}}/>
          </Col>
        </Row>
      
      </Container>

    </div>;
  }
}

export default App;
