import {Table} from 'react-bootstrap';
import {ToDoEntry} from '../model/ToDoEntry';

function List(props: {items: ToDoEntry[], onStatusChange: (user: ToDoEntry) => void}) {
  const rows = props.items.map((user) => (
    <tr key={user.id}>
      <td>{user.vorname}</td>
      <td>{user.nachname}</td>
      <td>{user.beschreibung}</td>
      <td>{user.date.toISOString()}</td>
      <td>
        <button className="btn btn-info" onClick={() => {props.onStatusChange(user)}}>
          {user.done ? 'UNDONE' : 'DONE'}
        </button>
      </td>
      <td>
        <button className="btn btn-info" onClick={() => {props.onStatusChange(user)}}>
          {user.done ? 'UNDONE' : 'DONE'}
        </button>
      </td>

    </tr>
  ));

  return (
    <Table>
      <thead>
      <tr>
        <th>Vorname</th>
        <th>Nachname</th>
        <th>Beschreibung</th>
        <th>ErstellungsDatum</th>
        <th>Action</th>
      </tr>
      </thead>
      <tbody>
      {rows}
      </tbody>
    </Table>
  );
}

export default List;
