import {Table} from 'react-bootstrap';
import {UserEntry} from '../model/ToDoEntry';
import React from 'react';

export class  List extends React.Component{
constructor (props: {items: UserEntry[]}) {
  super(props);

  let rows = props.items.map((user) => (
    <tr key={user.id}>
      <td>{user.vorname}</td>
      <td>{user.nachname}</td>
      <td>{user.beschreibung}</td>
      <td>{user.date.toISOString()}</td>
      <td>
        <button onClick={() => deleteUser(user.id)} className="btn btn-sm btn-danger btn-delete-user" >
         delete
        </button>
      </td>
      <td>
        <button className="btn btn-info" >
         bearbeiten
        </button>
      </td>

    </tr>
  ));

  function deleteUser(id:number) {
    console.log(props);
    props.items.filter(user=> user.id !==id)
    console.log(props.items.filter(user=> user.id !==id));
  }
  return (

    <Table>
      <thead>
      <tr>
        <th>Vorname</th>
        <th>Nachname</th>
        <th>Beschreibung</th>
        <th>ErstellungsDatum</th>
        <th>Action</th>
      </tr>
      </thead>
      <tbody>
      {rows}
      </tbody>
    </Table>
);


}  


}
export default List;
