import {Button, Form} from 'react-bootstrap';
import {useState} from 'react';

function FormUser(props:any) {

    const [vorname, setVorname] = useState('');
    const [nachname, setNachname] = useState('');
    const [beschreibung, setBeschreibung] = useState('');


    const handleChangeVorname =(e:any)=> {
      setVorname(e.target.value);

    }
    const handleChangeNachname =(e:any)=> {
      setNachname(e.target.value);
    }
    const handleChangebeschreibung=(e:any)=> {
      setBeschreibung(e.target.value);
    }

    const handleSubmit=(e:any)=> {
      e.preventDefault()
      props.onItem({
        vorname,
        nachname,
        beschreibung
      });
      setVorname('');
      setNachname('');
      setBeschreibung('');

    }

    return (
      <div>
        <h3>neue User</h3>
        <form onSubmit={(e) => handleSubmit(e)}>
            <div className="form-group">
                 <input value={vorname}
                        onChange={(e) => handleChangeVorname(e)}
                        type="text" name="vorname"placeholder="Vorname" style={{margin: '3em 10em 15em 10em', width:"30em"} }/>
            </div>
            <div className="form-group">
                <input value={nachname}
                        onChange={(e) => handleChangeNachname(e)}
                        type="text" name="nachname" placeholder="Nachname" style={{margin: '40em 3em'}}/>
            </div>
            <div className="form-group">
                 <textarea value={beschreibung}
                        onChange={(e) => handleChangebeschreibung(e)}
                         name="beschreibung" placeholder="Beschreibung" style={{margin: '40em 3em'}}/>
            </div>
          <Button variant="primary" type="submit">

            Submit
          </Button>
        </form>
      </div>
    );

}
export default FormUser;
