import React from 'react';
import './App.css';
import {Navbar, Button, Row, Col, Container} from 'react-bootstrap';
import {ToDoEntry} from './model/ToDoEntry';
import List from './components/List';
import Add from './components/Add';

class App extends React.Component<{}, { title: string, todos: ToDoEntry[] }> {
  constructor(props: {}) {
    super(props);
    this.state = {
      title: 'UserManager', todos: [
        new ToDoEntry('kevin','Piam','student'),
        new ToDoEntry('Winnie','Tongle','student'),
        new ToDoEntry('Maxwell','Tchiabe','Student')
      ]
    }
  }



  createToDo(vorname: string, nachname: string, beschreibung: string) {
    this.setState(state => ({
      todos: [...state.todos, new ToDoEntry(vorname,nachname,beschreibung)]
    }));
  }

  render() {
    return <div>
      <Navbar bg="light">
        <Navbar.Brand>{this.state.title}</Navbar.Brand>
      </Navbar>


    </div>;
  }
}

export default App;
